import React, {useEffect, useState} from 'react';
import { Col, Row, Button } from 'react-bootstrap';
import questions from './questions.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as functions from './utilityFunctions';
import Response from './Response';
import './App.css';

interface AnswerOptionProps {
    currentQuestion : number;
}

const AnswerOption : React.FunctionComponent<AnswerOptionProps> = (props: AnswerOptionProps) => {
    const answerType : string = questions[props.currentQuestion - 1].type;
    const options : string[] = questions[props.currentQuestion - 1].incorrect_answers;
    let answerSelected: boolean | null = false;
    let response: boolean | null = false;
    const checkAnswer: (optionChoosen: string) => void = (optionChoosen: string) => {
        answerSelected = true;
        if(optionChoosen == questions[props.currentQuestion - 1].correct_answer) {
            console.log("answer true");
            response = true;
        }
        else {
            console.log("answer false");
            response = false;
        }
    }
    options.push(questions[props.currentQuestion - 1].correct_answer);
    functions.shuffleArray(options);
    switch (answerType) {
        case 'multiple' : 
            return (
                <div>
                    <Row className = 'Option-row'>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(options[0]))}>
                                {decodeURIComponent(options[0])}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(options[1]))}>
                                {decodeURIComponent(options[1])}
                            </Button>
                        </Col>
                    </Row>
                    <Row className = 'Option-row'>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(options[2]))}>
                                {decodeURIComponent(options[2])}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(options[3]))}>
                                {decodeURIComponent(options[3])}
                            </Button>
                        </Col>
                    </Row>
                    <Row className = 'Option-row'>
                        <Response visible = {answerSelected} response = {response} />
                    </Row>
                </div>
            )
        break;
        case 'boolean' :
            return (
                <div>
                    <Row className = 'Option-row'>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(options[0]))}>
                                {decodeURIComponent(options[0])}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(options[1]))}>
                                {decodeURIComponent(options[1])}
                            </Button>
                        </Col>
                    </Row>
                    <Row className = 'Option-row'>
                        <Response visible = {answerSelected} response = {response}/>
                    </Row>
                </div>
            )
        break;
        default : 
            return (
                <div />
            )
        break;
    }
}

export default AnswerOption;
