import React, { useEffect, useState } from 'react';
import { Button, ProgressBar } from 'react-bootstrap';
import './App.css';
import questions from './questions.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import AnswerOption from './components/AnswerOption';
import EnterButton from './components/EnterButton';
import * as functions from './utility/utilityFunctions';

interface AppProps {
  noOfQuestions : number
}

const App : React.FunctionComponent<AppProps> = (props: AppProps) => {
  const [currentQuestion, setCurrentQuestion] = useState(1);
  const [noOfCorrectResponses, setNoOfCorrectResponses] = useState(0);
  const [noOfWrongResponses, setNoOfWrongResponses] = useState(0);
  let options : string[] = Object.assign([], questions[currentQuestion - 1].incorrect_answers);
  options.push(questions[currentQuestion - 1].correct_answer);
  console.log("initial",options);
  functions.shuffleArray(options);

  return (
    <div className = 'App'>
      <ProgressBar className = 'Progress-bar' animated now = {functions.printProgress(currentQuestion,props.noOfQuestions)} />
      <div className = 'Quiz'>
        <div className = 'Header'>
          <div className = 'Number'>
            Question {currentQuestion} of {props.noOfQuestions}
          </div>
          <div className = 'Category'>
            {decodeURIComponent(questions[currentQuestion - 1].category)}
          </div>
          <div className = 'Difficulty'>
          </div>
        </div>
        <div className = 'Question'>
          {decodeURIComponent(questions[currentQuestion - 1].question)}
        </div>
        <div className = 'Response'>
          <AnswerOption options = {options} currentQuestion = {currentQuestion} setCurrentQuestion = {setCurrentQuestion} setNoOfCorrectResponses = {setNoOfCorrectResponses} score = {functions.printScore(noOfCorrectResponses,noOfWrongResponses)} noOfQuestions = {props.noOfQuestions} noOfCorrectResponses = {noOfCorrectResponses} noOfWrongResponses = {noOfWrongResponses} setNoOfWrongResponses = {setNoOfWrongResponses} />
        </div>
      </div>
      <div className = 'Score-bar'>
        <div className = 'Score'>
          Score: {functions.printScore(noOfCorrectResponses,noOfWrongResponses)}%
        </div>
        <div className = 'Max-score'>
          Max Score: {functions.printMaxScore(noOfWrongResponses,props.noOfQuestions)}%
        </div>
        <div className = 'Bar'>
          <ProgressBar className = 'Progress' striped variant = 'success' now = {functions.printMinScore(noOfCorrectResponses,props.noOfQuestions)} />
          <ProgressBar className = 'Progress' striped variant = 'warning' now = {functions.printScore(noOfCorrectResponses,noOfWrongResponses)} />
          <ProgressBar className = 'Progress' striped variant = 'danger' now = {functions.printMaxScore(noOfWrongResponses,props.noOfQuestions)} />
          <ProgressBar className = 'Progress' striped variant = 'info' now = {100} />
        </div>
      </div>
    </div>
  )
}

export default App;
