import React, { useState } from 'react';
import { Button, ProgressBar } from 'react-bootstrap';
import './App.css';
import questions from './questions.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import AnswerOption from './AnswerOption';
import EnterButton from './EnterButton';

interface AppProps {
  noOfQuestions : number
}

const App : React.FunctionComponent<AppProps> = (props: AppProps) => {
  const [currentQuestion, setCurrentQuestion] = useState(1);
  const [noOfCorrectResponses, setNoOfCorrectResponses] = useState(0);
  const [responseSaved, checkResponse] = useState(false);
  const [registeredResponse, registerResponse] = useState(false);

  return (
    <div className = 'App'>
      <ProgressBar animated now = {printProgress(currentQuestion,props.noOfQuestions)} />
      <div className = 'Quiz'>
        <div className = 'Header'>
          <div className = 'Number'>
            Question {currentQuestion} of {props.noOfQuestions}
          </div>
          <div className = 'Category'>
            {decodeURIComponent(questions[currentQuestion - 1].category)}
          </div>
          <div className = 'Difficulty'>
          </div>
        </div>
        <div className = 'Question'>
          {decodeURIComponent(questions[currentQuestion - 1].question)}
        </div>
        <div className = 'Response'>
          <AnswerOption currentQuestion = {currentQuestion} registerResponse = {registerResponse} checkResponse = {checkResponse} />
        </div>
        <div className = 'Answer'>
          {printAnswer(registeredResponse,responseSaved, noOfCorrectResponses,setNoOfCorrectResponses)};
        </div>
        <EnterButton currentQuestion = {currentQuestion} setCurrentQuestion =  {setCurrentQuestion} score = {printScore(noOfCorrectResponses,currentQuestion)} noOfQuestions = {props.noOfQuestions} />
      </div>
      <div className = 'Score-bar'>
        <div className = 'Score'>
          Score: {printScore(noOfCorrectResponses,currentQuestion)}%
        </div>
        <div className = 'Max-score'>
          Max Score: {printMaxScore(noOfCorrectResponses,currentQuestion,props.noOfQuestions)}%
        </div>
        <div className = 'Bar'>
          <ProgressBar striped variant = 'success' now = {printMinScore(noOfCorrectResponses,currentQuestion,props.noOfQuestions)} />
          <ProgressBar striped variant = 'warning' now = {printScore(noOfCorrectResponses,currentQuestion)} />
          <ProgressBar striped variant = 'danger' now = {printMaxScore(noOfCorrectResponses,currentQuestion,props.noOfQuestions)} />
          <ProgressBar striped variant = 'info' now = {100} />
        </div>
      </div>
    </div>
  )
}

export default App;

function printProgress(currentQuestion: number, noOfQuestions: number) {
  return (currentQuestion/noOfQuestions) * 100;
}

function printScore(noOfCorrectResponses: number, currentQuestion: number) {
  return (noOfCorrectResponses/currentQuestion) * 100;
}

function printMaxScore(noOfCorrectResponses: number,currentQuestion: number,noOfQuestions: number) {
  return ((noOfCorrectResponses+(noOfQuestions-currentQuestion))/noOfQuestions) * 100;
}

function printMinScore(noOfCorrectResponses: number,currentQuestion: number,noOfQuestions: number) {
  return (noOfCorrectResponses/noOfQuestions) * 100;
}

function printAnswer(registeredResponse: boolean, responseSaved: boolean, noOfCorrectResponses: number, setNoOfCorrectResponses: (noOfCorrectResponses: number) => void) {
  if(registeredResponse == false) {
    return '';
  }
  else if(responseSaved) {
    setNoOfCorrectResponses(noOfCorrectResponses + 1);
    return 'Correct!';
  }
  else {
    return 'Sorry!';
  }
}
