import React from 'react';
import { Col, Row, Button } from 'react-bootstrap';
import questions from './questions.json';
import 'bootstrap/dist/css/bootstrap.min.css';

interface AnswerOptionProps {
    currentQuestion : number;
}

const AnswerOption : React.FunctionComponent<AnswerOptionProps> = (props: AnswerOptionProps) => {
    const answerType : string = questions[props.currentQuestion - 1].type;
    const options : string[] = questions[props.currentQuestion - 1].incorrect_answers;
    options.push(questions[props.currentQuestion - 1].correct_answer);
    shuffleArray(options);
    switch (answerType) {
        case 'multiple' : 
            return (
                <div>
                    <Row>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active>
                                {options[0]}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active>
                                {options[1]}
                            </Button>
                        </Col>
                    </Row>
                    <Row>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active>
                                {options[2]}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active>
                                {options[3]}
                            </Button>
                        </Col>
                    </Row>
                </div>
            )
        break;
        case 'boolean' :
            return (
                <div>
                    <Row>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active>
                                {options[0]}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active>
                                {options[1]}
                            </Button>
                        </Col>
                    </Row>
                </div>
            )
        break;
        default : 
            return (
                <div />
            )
        break;
    }
}

export default AnswerOption;

function shuffleArray(array: string[]) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
}
