import React, {useEffect, useState, useRef} from 'react';
import { Col, Row, Button } from 'react-bootstrap';
import questions from '../questions.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as functions from '../utility/utilityFunctions';
import Response from './Response';
import '../App.css';

interface AnswerOptionProps {
    currentQuestion : number;
    options: string[];
    setCurrentQuestion: (currentQuestion: number) => void;
    setNoOfCorrectResponses: (noOfCorrectResponses: number) => void;
    score: number;
    noOfQuestions: number;
}

const AnswerOption : React.FunctionComponent<AnswerOptionProps> = (props: AnswerOptionProps) => {
    const answerType : string = questions[props.currentQuestion - 1].type;
    const [answerSelected, answerChosen] = useState(false);
    const [response, responseCaptured] = useState(false);
    let disableButton: boolean = false;
    const checkAnswer: (optionChoosen: string) => void = (optionChoosen: string) => {
        console.log(props.options);
        if(!response) {
            disableButton = true;
            if(optionChoosen === questions[props.currentQuestion - 1].correct_answer) {
                console.log("answer true");
                answerChosen(true);
            }
            else {
                console.log("answer false");
                answerChosen(false);
            }
            responseCaptured(true);
        }
    }

    useEffect(() => {
        answerChosen(false);
        responseCaptured(false);
    }, [props.currentQuestion]);

    switch (answerType) {
        case 'multiple' : 
            return (
                <div>
                    <Row className = 'Option-row'>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active = {!disableButton} disabled = {disableButton} onClick = {checkAnswer.bind(this,decodeURIComponent(props.options[0]))}>
                                {decodeURIComponent(props.options[0])}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active = {!disableButton} disabled = {disableButton} onClick = {checkAnswer.bind(this,decodeURIComponent(props.options[1]))}>
                                {decodeURIComponent(props.options[1])}
                            </Button>
                        </Col>
                    </Row>
                    <Row className = 'Option-row'>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active = {!disableButton} disabled = {disableButton} onClick = {checkAnswer.bind(this,decodeURIComponent(props.options[2]))}>
                                {decodeURIComponent(props.options[2])}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active = {!disableButton} disabled = {disableButton} onClick = {checkAnswer.bind(this,decodeURIComponent(props.options[3]))}>
                                {decodeURIComponent(props.options[3])}
                            </Button>
                        </Col>
                    </Row>
                    <Row className = 'Option-row'>
                        <Response visible = {response} response = {answerSelected} />
                    </Row>
                    <Row>
                        <EnterButton visible = {response} currentQuestion = {props.currentQuestion} setCurrentQuestion =  {props.setCurrentQuestion}  setNoOfCorrectResponses = {props.setNoOfCorrectResponses} 
                            score = {props.score} noOfQuestions = {props.noOfQuestions} />
                    </Row>
                </div>
            )
        break;
        case 'boolean' :
            return (
                <div>
                    <Row className = 'Option-row'>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(props.options[0]))}>
                                {decodeURIComponent(props.options[0])}
                            </Button>
                        </Col>
                        <Col className = 'Options'>
                            <Button variant="primary" size="lg" active onClick = {checkAnswer.bind(this,decodeURIComponent(props.options[1]))}>
                                {decodeURIComponent(props.options[1])}
                            </Button>
                        </Col>
                    </Row>
                    <Row className = 'Option-row'>
                        <Response visible = {answerSelected} response = {response}/>
                    </Row>
                </div>
            )
        break;
        default : 
            return (
                <div />
            )
        break;
    }
}

export default AnswerOption;
