import React from 'react';

export function shuffleArray(array: string[]) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
}

export function printProgress(currentQuestion: number, noOfQuestions: number) {
    return (currentQuestion/noOfQuestions) * 100;
}

export function printScore(noOfCorrectResponses: number, currentQuestion: number) {
    return (noOfCorrectResponses/currentQuestion) * 100;
}

export function printMaxScore(noOfCorrectResponses: number,currentQuestion: number,noOfQuestions: number) {
    return ((noOfCorrectResponses+(noOfQuestions-currentQuestion))/noOfQuestions) * 100;
}

export function printMinScore(noOfCorrectResponses: number,currentQuestion: number,noOfQuestions: number) {
    return (noOfCorrectResponses/noOfQuestions) * 100;
}
