import React, { useEffect, useState } from 'react';
import { Button, ProgressBar } from 'react-bootstrap';
import './App.css';
import questions from './questions.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import AnswerOption from './components/AnswerOption';
import EnterButton from './components/EnterButton';
import * as functions from './utility/utilityFunctions';

interface AppProps {
  noOfQuestions : number
}

const App : React.FunctionComponent<AppProps> = (props: AppProps) => {
  const [currentQuestion, setCurrentQuestion] = useState(1);
  const [noOfCorrectResponses, setNoOfCorrectResponses] = useState(0);
  let options : string[] = questions[currentQuestion - 1].incorrect_answers;
  options.push(questions[currentQuestion - 1].correct_answer);
  functions.shuffleArray(options);

  return (
    <div className = 'App'>
      <ProgressBar className = 'Progress-bar' animated now = {functions.printProgress(currentQuestion,props.noOfQuestions)} />
      <div className = 'Quiz'>
        <div className = 'Header'>
          <div className = 'Number'>
            Question {currentQuestion} of {props.noOfQuestions}
          </div>
          <div className = 'Category'>
            {decodeURIComponent(questions[currentQuestion - 1].category)}
          </div>
          <div className = 'Difficulty'>
          </div>
        </div>
        <div className = 'Question'>
          {decodeURIComponent(questions[currentQuestion - 1].question)}
        </div>
        <div className = 'Response'>
          <AnswerOption option = {options} currentQuestion = {currentQuestion} />
        </div>
        <EnterButton currentQuestion = {currentQuestion} setCurrentQuestion =  {setCurrentQuestion} setNoOfCorrectResponses = {setNoOfCorrectResponses} score = {functions.printScore(noOfCorrectResponses,currentQuestion)} noOfQuestions = {props.noOfQuestions} />
      </div>
      <div className = 'Score-bar'>
        <div className = 'Score'>
          Score: {functions.printScore(noOfCorrectResponses,currentQuestion)}%
        </div>
        <div className = 'Max-score'>
          Max Score: {functions.printMaxScore(noOfCorrectResponses,currentQuestion,props.noOfQuestions)}%
        </div>
        <div className = 'Bar'>
          <ProgressBar className = 'Progress' striped variant = 'success' now = {functions.printMinScore(noOfCorrectResponses,currentQuestion,props.noOfQuestions)} />
          <ProgressBar className = 'Progress' striped variant = 'warning' now = {functions.printScore(noOfCorrectResponses,currentQuestion)} />
          <ProgressBar className = 'Progress' striped variant = 'danger' now = {functions.printMaxScore(noOfCorrectResponses,currentQuestion,props.noOfQuestions)} />
          <ProgressBar className = 'Progress' striped variant = 'info' now = {100} />
        </div>
      </div>
    </div>
  )
}

export default App;
