import React, {useState} from 'react';
import { Button, Modal } from 'react-bootstrap';

interface EnterButtonProps {
    currentQuestion: number;
    setCurrentQuestion: (currentQuestion: number) => void;
    score: number;
    noOfQuestions: number
}

const EnterButton : React.FunctionComponent<EnterButtonProps> = (props: EnterButtonProps) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const updateQuestion = () => {
        props.setCurrentQuestion(props.currentQuestion + 1);
    }

    if(props.currentQuestion == props.noOfQuestions) {
        return (
            <>
                <Button variant="primary" onClick={handleShow}>
                    End Test
                </Button>
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Score</Modal.Title>
                     </Modal.Header>
                    <Modal.Body>{props.score}</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                            Retake
                        </Button>
                    </Modal.Footer>
                </Modal>
            </>
        )
    }

    else {
        return (
            <>
                <Button variant="primary" onClick={updateQuestion}>
                    Next
                </Button>
            </>
        )
    }

}

export default EnterButton;
